#!/usr/bin/env python# -*- coding: utf-8 -*-# @Time    : 2020/9/26 0026 19:46# @Author  : Cojun  Mao# @Site    : # @File    : htmlescape.py# @Project : mysite_diy# @Software: PyCharmdef html_escape(string):    """ 转义HTML特殊字符`&amp;<>``和引号`````。 """    return string.replace('&', '&amp;').replace('<', '&lt;').replace('>', '&gt;') \        .replace('"', '&quot;').replace("'", '&#039;')def html_quote(string):    """ 转义并引用要用作HTTP属性的字符串。"""    return '"%s"' % html_escape(string).replace('\n', '&#10;') \        .replace('\r', '&#13;').replace('\t', '&#9;')